changeSet.sql:
create table if not exists CATEGORY ( ID bigserial constraint category_pk primary key, RELATION_ID integer, NAME varchar not null, STATUS boolean not null, UPDATED_AT timestamptz not null default CURRENT_TIMESTAMP, CREATED_AT timestamptz not null default CURRENT_TIMESTAMP );
alter table CATEGORY owner to dev_db;
create unique index category_id_uindex on CATEGORY (id);
